mapper.select:
select *, (select userDetail.name from userDetail where temp.senderCode = userDetail.userCode) as senderName, (select userDetail.name from userDetail where temp.receiverCode = userDetail.userCode) as receiverName 
from (select * from message where roomCode=#{roomCode} order by sendDate desc limit 35) temp 
order by sendDate;

mapper.update:
update message
set readFlag=1
where roomCode=#{roomCode} and receiverCode=#{receiverCode}
